public class _4A_Watermelon {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
			Scanner sc=new Scanner(System.in);
			
			short x=sc.nextShort();
			if(x%2==0 && x!=2) {
				System.out.println("Yes");
			}else {
				System.out.println("False");
			}
	}

}
